import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/components/pages/NovedadesPage.css';

const NovedadesPage = () => {
  const [novedades, setNovedades] = useState([]);

  useEffect(() => {
    const cargarNovedades = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/novedades');
        setNovedades(response.data);
      } catch (error) {
        console.error('Error al cargar novedades:', error);
      }
    };
    cargarNovedades();
  }, []);

  return (
    <section className="novedades-container">
      <h2 className="novedades-titulo">ðŸ“° Novedades</h2>

      {novedades.map((item) => (
        <article key={item.id} className="novedad-card">
          <h1>{item.titulo}</h1>
          <h2>{item.subtitulo}</h2>
          <p>{item.cuerpo}</p>
        </article>
      ))}
    </section>
  );
};

export default NovedadesPage;
